import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.scss']
})
export class GameComponent implements OnInit {

  squares : any = [];
  xIsNext = true;
  winner = '';
  counter = 0;
  isDraw = '';
  freshPage = true;


  constructor () {}

  ngOnInit(): void {

  }

  newGame() {
    this.squares  = Array (9).fill(null);
    this.winner = '' ;
    this.isDraw = '';
    this.counter = 0;
    this.freshPage = false;

  }

  get player(){
     return this.xIsNext? 'X' : 'O';
  }

  makeMove(idx:number){
    if (!this.squares[idx]){

      this.squares.splice(idx, 1, this.xIsNext)
      this.xIsNext = !this.xIsNext;
      this.counter++;

    }
    this.counter = this.calculateWinner();
  }

    calculateWinner(){
    //  0 1 2
    //  3 4 5
    //  6 7 8
      const lines = [
        [0,1,2],[2,3,5],[6,7,8],
        [0,3,6],[1,4,7],[2,5,8],
        [0,4,8],[6,4,2]
      ]
      for ( let i=0 ; i< lines.length, i)

    }
